	var arr = [20, 19, 21, 21, 21, 22, 23, 23, 23, 23, 22, 22];

	return s.substr(cMonth * 2 - (cDay < arr[cMonth - 1] ? 2 : 0), 2) + "\u5ea7"; // 座
}

