/**

* 传入农历日期数字返回汉字表示法

* @param lunar day

* @return Cn string

* @eg:var cnDay = calendar.toChinaDay(21) ;//cnMonth='廿一'

*/

function toChinaDay(d) {
	// 日 => \u65e5

	var s;

	switch (d) {
		case 10:
			s = "\u521d\u5341";

			break;

		case 20:
			s = "\u4e8c\u5341";

			break;

		case 30:
			s = "\u4e09\u5341";

			break;

		default:
			s = nStr2[Math.floor(d / 10)];

			s += nStr1[d % 10];
	}

	return s;
}

