function winResize() {
	try {
		var offsetWid = document.documentElement.clientWidth;
		if (offsetWid <= 768) {
			winbox.resize(offsetWid * 0.95 + "px", "90%").move("center", "center");
		} else {
			winbox.resize(offsetWid * 0.6 + "px", "70%").move("center", "center");
		}
	} catch (err) {
		console.log("Pjax毒瘤抽风运行winResize方法🙄🙄🙄");
	}
}

